import postContext from './postContext';


export default function NoteState(props) {
    const host = "http://localhost:4000"
    const allpost =[]

    const [posts, SetPost] = useState(allpost)

    //get all notes
    const getpost=async()=>{

        //API CALL
        const resposne = await fetch(`${host}/posts/`, {
            method: "GET",
            headers: {
                'Content-Type': 'application/json',
            },

        });
        const json = await resposne.json()
        SetPost(json);


    }

    // add a note 
    const addpost = async (newnote) => {
        const Title = newnote.Title;
        const Description = newnote.Description;

        //API CALL
        const resposne = await fetch(`${host}/notes/addnote`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
            body: JSON.stringify({ Title, Description })

        });
        const json = resposne.json()
        getpost()
    }

    //delete a note






    return (
        <postContext.Provider value={{ posts, addpost,getpost}}>
            {props.children}
        </postContext.Provider>
    )
}
