import React from 'react'
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { StickyContainer, Sticky } from 'react-sticky';

export default function Sidebar() {
  const navigate = useNavigate();

  const handleclick = () => {
    localStorage.removeItem('token');
    navigate("/signup");
    alert("logged out successfully")
  }



  return (
    <div style={{

      height:'100%',

      }}>

      <nav  style={{
         backgroundColor: '#5C5470',
         width:'250px', 
         minHeight:'100%',
         position: 'sticky',
         top: 0,
         justifycontent: 'flex-start',
         }}>
        <div>
          <div style={{
         border:'2px solid white'
         }}>
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className="nav-link " aria-current="page" to="/posts"  style={{
         border:'2px solid white'
         }}>Posts </Link>
              </li>
            </ul>

            {!localStorage.getItem('token') ?
              <form class="" role="search">
                <Link class="btn btn-outline-light mx-1" role="button" to="/login">Login</Link>
                <Link class="btn btn-outline-light mx-1" role="button" to="/signup">Sign Up</Link></form>
              :
              <button class="btn btn-outline-light mx-1 d-flex" onClick={handleclick} >Logout</button>

            }

          </div>


        </div>
      </nav>

    </div>
  )
}
