import React, { useState } from 'react'
import postContext from './postContext';


export default function NoteState(props) {
    const host = "http://localhost:4000"


    const [allPosts, setPost] = useState([]);
    const [hasMore, setHasMore] = useState(true);



    //get posts 
    const getpost = async (page = 1, reset = false) => {
        // Retrieve category engagement from local storage
        const categoryData = JSON.parse(localStorage.getItem("categoryEngagement")) || {};

        // Sort categories based on engagement count (highest first)
        const sortedCategories = Object.entries(categoryData)
            .sort(([, a], [, b]) => b - a); // Sort by count descending

        // Calculate total engagement count
        const totalEngagement = sortedCategories.reduce((sum, [, count]) => sum + count, 0);

        // Determine how many posts should be fetched per category based on percentage engagement
        const categoryDistribution = sortedCategories.map(([category, count]) => ({
            category,
            percentage: totalEngagement > 0 ? (count / totalEngagement) : (1 / sortedCategories.length) // Default even distribution if no engagement
        }));

        // Calculate how many posts per category (10 total posts)
        const categoryPostCounts = categoryDistribution.map(({ category, percentage }) => ({
            category,
            count: Math.round(percentage * 10) // Fetch proportional posts
        }));

        // Fetch posts dynamically for each category
    let allPosts = [];
    for (const { category, count } of categoryPostCounts) {
        const response = await fetch(`${host}/posts?page=${page}&limit=${count}&categories=${category}`, {
            method: "GET",
            headers: {
                'Content-Type': 'application/json',
            },
        });

        const posts = await response.json();
        allPosts = [...allPosts, ...posts];
    }

    // If reset is true, replace posts, otherwise append new posts
    if (reset) {
        SetPost(allPosts);
    } else {
        SetPost(prevPosts => [...prevPosts, ...allPosts]);
    }

        if (json.length < 20) {
            setHasMore(false);  // No more posts to load
        }
    };

    // add a post
    const addpost = async (newpost) => {
        const Title = newpost.Title;
        const Subheading = newpost.Subheading;
        const Description = newpost.Description;
        const Category = newpost.Category;


        //API CALL
        const resposne = await fetch(`${host}/posts/newpost`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
            body: JSON.stringify({ Title, Subheading, Description, Category })

        });
        const json = resposne.json()
        getpost()
    }





    return (
        <postContext.Provider value={{ posts, addpost, getpost }}>
            {props.children}
        </postContext.Provider>
    )
}
