import React from 'react'
import Displaypost from './Displayposts';
import { useContext, useEffect,useState } from 'react'
import postContext from '../context/postContext'
import Masonry from "masonry-layout";

export default function Posts() {

  const { posts, getpost } = useContext(postContext);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);

  useEffect(() => {   
    const fetchInitialPosts = async () => {
      setLoading(true);
      await getpost(1, true);
      setLoading(false);
    };
    fetchInitialPosts();
  }, []);


// masonry 
useEffect(() => {
  new Masonry(".masonry-grid", {
    itemSelector: ".masonry-item",
    columnWidth: ".masonry-item",
    percentPosition: true,
    gutter: 0,
  });
}, [posts]);


// load more button 
const loadMore = async () => {
  setLoading(true);
  await getpost(page + 1, false);
  setPage(page + 1);
  setLoading(false);
};


// dynamic recommendation system 
const categories = [
  "Technology",
  "Health & Wellness",
  "Education & Learning",
  "Lifestyle & Productivity",
  "Entertainment & Pop Culture",
  "General"
];

function storeCategories() {
  const categoryData = categories.reduce((acc, category) => {
    acc[category] = 0; // Initialize each category with count 0
    return acc;
  }, {});

  localStorage.setItem("categoryEngagement", JSON.stringify(categoryData));
}

const resetCategoryEngagement = () => {
  const resetData = categories.reduce((acc, category) => {
      acc[category] = 0;
      return acc;
  }, {});
  localStorage.setItem("categoryEngagement", JSON.stringify(resetData));
  console.log("Category engagement reset to 0");
};

useEffect(() => {
  const storedData = localStorage.getItem("categoryEngagement");
  if (!storedData) {
    storeCategories();
  } else {
    console.log("Existing Category Data:", JSON.parse(storedData));
  }
}, []);

  return (
    <div className='row masonry-grid' style={{ marginLeft: "-10px", width: "80vw" }}>
      {loading && page === 1 ? (
        <div className="d-flex justify-content-center align-items-center py-5 my-5 w-100">
          <div className="text-center">
            <div className="spinner-border text-primary" style={{ width: '3rem', height: '3rem' }} role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <h5 className="mt-3 text-muted">Loading posts...</h5>
            <p className="text-muted">Please wait while we fetch the latest content</p>
          </div>
        </div>
      ) : (
        posts.map((post) => <Displaypost key={post._id} post={post} />)
      )}

      <button
        onClick={resetCategoryEngagement} 
        className="btn btn-danger btn-sm d-block mx-auto my-3 mb-2"
      >
        Reset Engagement Data
      </button>

      {posts.length > 0 && (
        <button 
          onClick={loadMore} 
          disabled={loading} 
          className="btn btn-primary btn-lg d-block mx-auto my-4 px-4"
        >
          {loading ? (
            <>
              <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
              Loading...
            </>
          ) : (
            'Load More Posts'
          )}
        </button>
      )}
    </div>
  )
}
