import React from 'react'
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { StickyContainer, Sticky } from 'react-sticky';

export default function Sidebar() {
  const navigate = useNavigate();

  const handleclick = () => {
    localStorage.removeItem('token');
    navigate("/signup");
    alert("logged out successfully")
  }



  return (
    <div style={{

      height: '100%',

    }}>

      <nav style={{
        backgroundColor: '#5C5470',
        width: '20vw',
        minHeight: '100%',
        position: 'sticky',
        top: 0,
        justifycontent: 'flex-start',
      }}>
        <div>
          <div style={{
            //border: '2px solid white'
          }}>
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">


            </ul>

            {!localStorage.getItem('token') ?

              <form  role="search">
                <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                <li className="nav-item">         
                <Link  className="bi bi-postcard-heart-fill display-6 text-decoration-none" aria-current="page" to="/posts" style={{
                  border: '2px solid white',
                  color: 'white',
                }}>   Posts </Link>
              </li>
                  <li>
                  <Link className="bi bi-box-arrow-in-right display-6 text-decoration-none"  aria-current="page"  to="/login" style={{
                  border: '2px solid white',
                  color: 'white',
                }}>Login</Link>
                  </li>
                  <li>
                  <Link className="bi bi-check-circle-fill display-6 text-decoration-none"  aria-current="page"  to="/signup" style={{
                  border: '2px solid white',
                  color: 'white',
                }}>Sign Up</Link>
                  </li>                        
                </ul>



                </form>
              :

              <div style={{
                marginTop:'30vh',
                display: 'flex',
                left:'20px',
                position:'fixed'                 
              }} >
                <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                <li className="nav-item" style={{
                  width:'700px',
                }}>         
                <Link  className="bi bi-postcard-heart-fill display-6 text-decoration-none" aria-current="page" to="/posts" style={{

                  color: 'white',

                }}>   Posts </Link>
              </li>

              <li className="nav-item">
                <Link className="bi bi-house-door-fill display-6 text-decoration-none" aria-current="page" to="/"  style={{

                  color: 'white',
                }}> Home </Link>
              </li>

              <li className="nav-item">
                <Link className="bi bi-person-circle display-6 text-decoration-none" aria-current="page" to="/"  style={{

                  color: 'white',
                }}> Profile </Link>
              </li>

              <li>
              <button className="bi bi-box-arrow-left display-6 text-decoration-none" onClick={handleclick} 
              style={{
                marginTop:'30vh',
                border:'0px',
                backgroundColor:'#5C5470',
              }}
              > Logout</button>
                </li>

            </ul>

              </div>



            }

          </div>


        </div>
      </nav>

    </div>
  )
}
