import React from 'react'
import '../App.css';
import { useContext, useEffect,useState } from 'react'
import postContext from '../context/postContext'

export default  function Displayposts(props) {
    const { user,userdetails } = useContext(postContext);

    const [userData, setUserData] = useState({ name: '', email: '' }); // Local state for name and email

    const { post } = props;

    useEffect(() => {
        async function fetchUserDetails() {
             await userdetails(post.User); // Assuming userdetails returns user data
            setUserData({ name: user.name, email: user.email }); // Update local state
        }
        fetchUserDetails();
    }, [post.User, userdetails]);



    const col = ["info", "warning", "primary", "success", "danger", "secondary"]
    return (

        <div className='col-md-4 ' >
            <div className={`  my-3 p-3 bg-${col[Math.floor(Math.random() * 6)]} bg-opacity-10 border-info  rounded-end`}>

            <h2 className="card-title">{userData.name}</h2>
            <h6 className="card-title">{userData.email}</h6>
            <br/>

                <h4 className="card-title">{post.Title}</h4>
                <h6 className="card-title">{post.User}</h6>
                <p className="  box ">{post.Description}</p>

            </div>



        </div>





    )
}
