import React, { useState } from 'react'
import postContext from './postContext';


export default function NoteState(props) {
    const host = "http://localhost:4000"
    const allpost =[]
    const userinfo =[]

    const [posts, SetPost] = useState(allpost)
    const [user, Setuser] = useState(userinfo)


    //get all notes
    const getpost=async()=>{
        //API CALL
        const resposne = await fetch(`${host}/posts/`, {
            method: "GET",
            headers: {
                'Content-Type': 'application/json',
            },

        });
        const json = await resposne.json()
        SetPost(json);

    }

    // add a post
    const addpost = async (newpost) => {
        const Title = newpost.Title;
        const Subheading = newpost.Subheading;
        const Description = newpost.Description;

        //API CALL
        const resposne = await fetch(`${host}/posts/newpost`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
            body: JSON.stringify({ Title,Subheading, Description })

        });
        const json = resposne.json()
        getpost()
    }

    // get user details 
    const userdetails = async(id)=>{
        // API CALL
        const response = await fetch(`${host}/posts/getuser/${id}`,{
            method :"POST",
            headers: {
                'Content-Type': 'application/json',
            },

        });

        const json = await resposne.json()
        Setuser(json);

    }



    return (
        <postContext.Provider value={{ posts,addpost,getpost,user,}}>
            {props.children}
        </postContext.Provider>
    )
}
