import React from 'react'
import '../App.css';
import { useContext, useState } from 'react'
import noteContext from '../context/noteContext'


export default function Display(props) {

    const { deletenote, editnote } = useContext(noteContext);

    //for edit node

    //getting the id 
    const [idd, setid] = useState("");
    const handleid = () => {
        console.log(note._id)
        setid(note._id)

    }



    //getting the data
    const [title, settitle] = useState("");
    const [desc, setdesc] = useState("");
    const handletitle = (event) => {
        settitle(event.target.value);
    }
    const handledesc = (event) => {
        setdesc(event.target.value);
    }
    const handleclick = () => {
        const newnote = {
            Title: title,
            Description: desc

        }
        editnote(idd,newnote);
        console.log(idd)
    }




    const { note } = props;
    const col = ["info", "warning", "primary", "success", "danger", "secondary"]
    return (

        <div className='col-md-4 ' >
            <div className={`  my-3 p-3 bg-${col[Math.floor(Math.random() * 6)]} bg-opacity-10  border-info  rounded-end`}>
                <h5 className="card-title">{note.Title}</h5>
                <p className="  box ">{note.Description}</p>

                <div>
                    <span className="material-symbols-outlined icon" onClick={() => { deletenote(note._id) }}>delete</span>
                    <span className="material-symbols-outlined icon" data-bs-toggle="modal" data-bs-target="#staticBackdrop" onClick={handleid}>edit</span>
                    <div>
                    <div className="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
            <div className="modal-dialog">
                <div className="modal-content">
                    <div className="modal-header">
                        <h1 className="modal-title fs-5" id="staticBackdropLabel">Edit your note </h1>
                        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                    </div>
                    <div className="modal-body">
                    <div className="mb-3 container">
                            <label for="exampleFormControlInput1" className="form-label">Title</label>
                            <input type="text" className="form-control" id="exampleFormControlInput1" name='Title' value={title} onChange={handletitle} />
                        </div>
                        <div className="mb-3 container">
                            <label for="exampleFormControlTextarea1" className="form-label">Description</label>
                            <input type="text" className="form-control" id="exampleFormControlInput1" name='Description' value={desc} onChange={handledesc} />

                        </div>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-primary" data-bs-dismiss="modal" onClick={handleclick}>Save</button>
                    </div>
                </div>
            </div>
        </div>
                    </div>
                </div>
            </div>

        </div>





    )
}
