import React from 'react'
import '../App.css';
import { useState, useEffect } from 'react'
import Modal from 'react-bootstrap/Modal';
import bg from './images/bg1.jpeg'




export default function Displayposts(props) {
    const { post } = props;

    // get user details
    const host = "http://localhost:4000"
    const userinfo = {}
    const [user, Setuser] = useState(userinfo)


    const userdetails = async (id) => {
        // API CALL
        const response = await fetch(`${host}/posts/getuser/${id}`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
            },

        });

        const json = await response.json()
        Setuser(json)
    }
    // map user details 
    useEffect(() => {
        userdetails(post.User);

    }, [])

    //console.log(name);
    // var arr = name.split(" ");


    // for post as button
    const [modalShow, setModalShow] = React.useState(false);
    const handleshow = () => { setModalShow(true) };
    const handleclose = () => { setModalShow(false) };

// like the post
const [like, setlike] = React.useState("bi bi-heart");
const handlelike = () => { 
    if(like==("bi bi-heart")){
    setlike("bi bi-heart-fill");}
    else{setlike("bi bi-heart")}

 };

 // save the post
const [save, setsave] = React.useState("bi bi-bookmark-heart");
 const handlesave = () => { 
    if(save==("bi bi-bookmark-heart")){
        setsave("bi bi-bookmark-heart-fill");}
    else{setsave("bi bi-bookmark-heart")}
 };









    const col = ["info", "warning", "primary", "success", "danger", "secondary"]
    return (

        <div className='col-md-4 ' >
            <div className={`  my-3 p-3  bg-opacity-10 border-info  rounded-end cursor-pointer `}
            style={{
                backgroundImage: `url(${bg})`,
                backgroundRepeat:"no-repeat",
                backgroundSize:"100% 100%",
                opacity:'0.5',



            }} >
                <div onClick={handleshow} style={{cursor:"pointer",
                    opacity:'1'
                }}>
                    <p className="card-title fs-4"> <b>{post.Title}</b></p>
                    <p className="card-title fs-5 ">{post.Subheading}</p>
                </div>
                <hr/>
                <div>
                    <div className="  fs-5  d-flex flex-row" >
                        <p className={`${like} m-0 p-0 `} 
                        style={{cursor:"pointer",
                        }}
                        onClick={handlelike} ></p>
                        <p className={`${save}  text-end my-0 mx-3`} 
                       style={{cursor:"pointer"}}

                        onClick={handlesave} ></p>
                    </div>
                </div>

            </div>

            <Modal
                show={modalShow}
                onHide={handleclose}
                backdrop="static"
                keyboard={false}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered

            >


                <Modal.Body 
                className={`  m-2 my-2 p-3 bg-${col[Math.floor(Math.random() * 6)]} bg-opacity-10 border-info  rounded-end  my-0`}

                >
            <button type="button" class="btn-close" aria-label="Close" onClick={handleclose}></button>
            <br></br>
            <br></br>

                <div onClick={handleshow}>
                <div >
                    <p className="card-title fs-2"> <b>{post.Title}</b></p>
                    <p className="card-title fs-4 ">{post.Subheading}</p>
                    <p className="  box fs-6 lh-sm">{post.Description}</p>
                </div>
                <br />
                <div className="  fs-5  d-flex flex-row" >
                        <p className={`${like} m-0 p-0 `} 
                        style={{cursor:"pointer",
                        }}
                        onClick={handlelike} ></p>
                        <p className={`${save}  text-end my-0 mx-3`} 
                       style={{cursor:"pointer"}}

                        onClick={handlesave} ></p>
                    </div>
                <div style={{ display: 'flex' }}>
                    <div className="  box fs-6 ">
                        <p className="card-title text-end"><b>{user.name}</b></p>
                        <p className="card-title text-end ">{user.email}</p>
                    </div>
                </div>
            </div>

                </Modal.Body>        
            </Modal>

        </div>

    )
}
