  "#d8d8d8",
  "#ffc0cb",
  "#90b499",
  "#ffdcfd",];




const TopNavbar = () => {


  const [text, setText] = useState('');
  const [num, setnum] = useState(0);



  return (



    // following style elements are used to make navbar sticky 

