import React from 'react'
import { useState, useEffect } from 'react'
import img from './images/img.png'
import title from './images/title.png'

export default function About() {
  const userinfo = {}
  const [user, setUser] = useState(userinfo)

  const host = "https://unsrecordbend.onrender.com"

  const getdetails = async () => {
    const response = await fetch(`${host}/auth/getuser`, {
      method: "POST",
      headers: {
        'Content-Type': 'application/json',
        'auth-token': localStorage.getItem('token')
      },
    });
    const json = await response.json()
    setUser(json)
  }

  useEffect(() => {
    getdetails();
  }, [])

  return (
    <div className='container-fluid p-4'>
      <p className='fs-2 my-5 text-center fst-italic'> 
        The ultimate hub for note sharing and discovery<br />
        You, our passionate contributors, are what make this community so special!
      </p>

      <div className="container d-flex justify-content-center my-5">
        <div className="row g-0 bg-purple-light rounded-4 p-3" style={{
          backgroundColor: '#ded1ff',
          maxWidth: '800px'
        }}>
          <div className="col-12 col-md-8 mx-auto bg-white rounded-4 p-3">
            <div className="d-flex flex-column flex-md-row align-items-center">
              <div className="me-md-4 mb-3 mb-md-0" style={{ width: '120px' }}>
                <img 
                  src={img} 
                  alt="Profile" 
                  className="img-fluid rounded-circle" 
                  style={{ border: '3px solid #ded1ff' }}
                />
              </div>
              <div className="text-center text-md-start">
                <h2 className="fw-bold mb-1">{user.name || 'User Name'}</h2>
                <p className="text-muted mb-0">{user.email || 'user@example.com'}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container-fluid px-0">
        <div className="row justify-content-end g-0">
          <div className="col-12 col-lg-10 position-relative">
            <img 
              src={title} 
              alt="About our community" 
              className="img-fluid float-end"
              style={{
                maxWidth: '80%',
                borderLeft: '15px solid #5C5470',
                borderRadius: '200px 0 0 200px',
                padding: '30px',
                backgroundColor: '#f8f9fa'
              }} 
            />
          </div>
        </div>
      </div>
    </div>
  )
}
