import React from 'react'
import '../App.css';
import {useState,useEffect } from 'react'


export default  function Displayposts(props) {
    const { post } = props;

    // get user details
    const host = "http://localhost:4000"
    const userinfo ={}
    const [user, Setuser] = useState(userinfo)
    const userdetails = async(id)=>{
        // API CALL
        const response = await fetch(`${host}/posts/getuser/${id}`,{
            method :"POST",
            headers: {
                'Content-Type': 'application/json',
            },

        });

        const json = await response.json()
        Setuser(json)
    }
    // map user details 
    useEffect(()=>{   
        userdetails(post.User); 
    },[])




    const arr= user.name.split(" ");

    const col = ["info", "warning", "primary", "success", "danger", "secondary"]
    return (

        <div className='col-md-4 ' >
            <div className={`  my-3 p-3 bg-${col[Math.floor(Math.random() * 6)]} bg-opacity-10 border-info  rounded-end`}>
            <div style={{display:'flex'}}>
                <div style={{
                    backgroundColor: '#DBD8E3',
                    position: 'relative',
                    margin:'10px',
                    padding: '10px',
                    backgroundColor:'#352f44',
                    borderRadius:"30%",
                    color:'white'
                    }}>
                    <h3>{arr[0][0]+arr[1][0].toUpperCase()}</h3>
                </div>
                <div style={{
                    position: 'relative',

                    padding: '10px',


                    }}>
                    <h3 className="card-title"><b>{user.name}</b></h3>
                    <h6 className="card-title">{user.email}</h6>
                </div>

            </div>

            <br/>

                <h4 className="card-title">{post.Title}</h4>
                <h6 className="card-title">{post.User}</h6>
                <p className="  box ">{post.Description}</p>

            </div>



        </div>





    )
}
