import React from 'react'
import {  useState , useEffect} from 'react'
import logo from '../components/images/img.png'



export default function About() {
  const userinfo = {}
  const [user, Setuser] = useState(userinfo)

    const host = "http://localhost:4000"

    const getdetails = async ()=>{
        const resposne = await fetch(`${host}/auth/getuser`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
        });
        const json = await resposne.json()
        Setuser(json)       
    }
    useEffect(() => {
      getdetails();

  }, [])

    return (
        <div className="container my-5 d-flex flex-row">
          <div className="bi bi-person-fill-check  mx-5 w-25">

          </div>

            <div>

            <p className="fs-1" >{user.name}</p>
            <hr></hr>
            <p className="fs-1" >{user.email}</p>

            </div>


        </div>
    )
}
