import React from 'react'
import '../App.css';
import { useState, useEffect } from 'react'
import Modal from 'react-bootstrap/Modal';



export default function Displayposts(props) {
    const { post } = props;

    // get user details
    const host = "http://localhost:4000"
    const userinfo = {}
    const [user, Setuser] = useState(userinfo)


    const userdetails = async (id) => {
        // API CALL
        const response = await fetch(`${host}/posts/getuser/${id}`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
            },

        });

        const json = await response.json()
        Setuser(json)
    }
    // map user details 
    useEffect(() => {
        userdetails(post.User);

    }, [])

    //console.log(name);
    // var arr = name.split(" ");


    // for post as button
    const [modalShow, setModalShow] = React.useState(false);
    const handleshow = () => { setModalShow(true) };
    const handleclose = () => { setModalShow(false) };

// like the post
const [like, setlike] = React.useState("bi bi-heart");
const handlelike = () => { setlike("bi bi-heart-fill") };

const handleboth=()=>{
    handlelike();
    handleclose()
}







    const col = ["info", "warning", "primary", "success", "danger", "secondary"]
    return (

        <div className='col-md-4 ' >
            <div className={`  my-3 p-3 bg-${col[Math.floor(Math.random() * 6)]} bg-opacity-10 border-info  rounded-end cursor-pointer `} onClick={handleshow} style={{cursor:"pointer"}}>
                <div >
                    <p className="card-title fs-4"> <b>{post.Title}</b></p>
                    <p className="card-title fs-5 ">{post.Subheading}</p>
                </div>
                <br />
                <div style={{ display: 'flex' }}>
                    <div className="  box fs-6 ">
                        <p className={`${like}`} 
                        style={{cursor:"default"}}
                        onClick={handlelike} ></p>
                    </div>
                </div>

            </div>

            <Modal
                show={modalShow}
                onHide={handleclose}
                backdrop="static"
                keyboard={false}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >

                <Modal.Body>
            <button type="button" class="btn-close" aria-label="Close" onClick={handleclose}></button>
                <div className={`  my-3 p-3 bg-${col[Math.floor(Math.random() * 6)]} bg-opacity-10 border-info  rounded-end `} onClick={handleshow}>
                <div >
                    <p className="card-title fs-2"> <b>{post.Title}</b></p>
                    <p className="card-title fs-4 ">{post.Subheading}</p>
                    <p className="  box fs-6 lh-sm">{post.Description}</p>
                </div>
                <br />
                <div style={{ display: 'flex' }}>
                    <div className="  box fs-6 ">
                        <p className="card-title text-end"><b>{user.name}</b></p>
                        <p className="card-title text-end ">{user.email}</p>
                    </div>
                </div>
            </div>

                </Modal.Body>        
            </Modal>

        </div>

    )
}
