import React from 'react'
import { useState, useEffect } from 'react'
import img from '../components/images/img.png'
import title from '../components/images/title.png'




export default function About() {
  const userinfo = {}
  const [user, Setuser] = useState(userinfo)

  const host = "http://localhost:4000"

  const getdetails = async () => {
    const resposne = await fetch(`${host}/auth/getuser`, {
      method: "POST",
      headers: {
        'Content-Type': 'application/json',
        'auth-token': localStorage.getItem('token')
      },
    });
    const json = await resposne.json()
    Setuser(json)
  }
  useEffect(() => {
    getdetails();

  }, [])

  return (
    <div className=''>
      <p className='fs-2 m-5'> The ultimate hub for note sharing and discovery<br />
        You, our passionate contributors, are what make this community so special !
      </p>

      <div className="container d-flex flex-rowreverse" style={{
        backgroundColor: '#ded1ff',
        padding: '20px',
        borderRadius: '200px',
        margin: '30px',
        width: '50%'



      }}>
        <div className="container d-flex flex-rowreverse" style={{
          backgroundColor: 'white',
          padding: '10px',
          borderRadius: '200px'
        }}>
          <div style={{
            width: '10vw',
          }} >
            <img src={img} alt="img" style={{ width: '100%', }} />
          </div>
          <div style={{

            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            margin: '0px'
          }}>
            <div className=" container fs-2 fw-bold" style={{

            }} >
              {user.name}<br />
              {user.email}
            </div>
          </div>

        </div>

      </div>
      <div  className ='justify-content-endjustify-content-end ' style={{
        width: '100vw',
        alignContent:'right'
      }} >
        <img src={title} alt="img" style={{ width: '40%', }} />
      </div>

    </div>

  )
}
