import React from 'react'
import '../App.css';
import { useState, useEffect } from 'react'
import Modal from 'react-bootstrap/Modal';



export default function Displayposts(props) {
    const { post } = props;

    // get user details
    const host = "http://localhost:4000"
    const userinfo = {}
    const [user, Setuser] = useState(userinfo)


    const userdetails = async (id) => {
        // API CALL
        const response = await fetch(`${host}/posts/getuser/${id}`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
            },

        });

        const json = await response.json()
        Setuser(json)
    }
    // map user details 
    useEffect(() => {
        userdetails(post.User);

    }, [])

    //console.log(name);
    // var arr = name.split(" ");


    // for post as button
    const [modalShow, setModalShow] = React.useState(false);
    const onHide = () => {
        setModalShow(false);
    }





    const col = ["info", "warning", "primary", "success", "danger", "secondary"]
    return (

        <div className='col-md-4 ' >
            <div className={`  my-3 p-3 bg-${col[Math.floor(Math.random() * 6)]} bg-opacity-10 border-info  rounded-end `}>
                <div onClick={() => setModalShow(true)}>
                    <p className="card-title fs-2"> <b>{post.Title}</b></p>
                    <p className="card-title fs-4 ">{post.Subheading}</p>
                    <p className="  box fs-6 lh-sm">{post.Description}</p>

                    <Modal show={modalShow}
          onHide={() => setModalShow(false)}

      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Add a new post
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>


      </Modal.Body>
      <Modal.Footer>
      <button type="button" className="btn btn-primary" data-bs-dismiss="modal" 
        onClick={onHide}>Close</button>
      </Modal.Footer>
    </Modal>
                </div>
                <br/>
                <div style={{ display: 'flex' }}>
                    <div className="  box fs-6 ">
                        <p className="card-title text-end"><b>{user.name}</b></p>
                        <p className="card-title text-end ">{user.email}</p>
                    </div>
                </div>
            </div>
        </div>
    )
}
