import React from 'react'
import Displaypost from './Displayposts';
import { useContext, useEffect,useState } from 'react'
import postContext from '../context/postContext'
import Masonry from "masonry-layout";

export default function Posts() {

    const {posts,getpost} =useContext(postContext);
    useEffect(()=>{   
        getpost(); 
    },[])


    // masonary
    useEffect(() => {
      // Initialize Masonry after component mounts
      new Masonry(".masonry-grid", {
        itemSelector: ".masonry-item",
        columnWidth: ".",
        percentPosition: true,
        gutter: 0, // Space between items
      });
    }, []);


  return (

       <div className='row masonry-grid'>      
        {// all the posts are here 
        posts.map((post)=> {return < Displaypost post = {post }/>})}

      </div>

  )
}
