import React from 'react'
import Displaypost from './Displayposts';
import { useContext, useEffect,useState } from 'react'
import postContext from '../context/postContext'
import Masonry from "masonry-layout";

export default function Posts() {

  const { posts, getpost } = useContext(postContext);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);

  useEffect(() => {   
    getpost(1, true);  // Load the first page with reset
}, []);


// masonary 
useEffect(() => {
  new Masonry(".masonry-grid", {
    itemSelector: ".masonry-item",
    columnWidth: ".masonry-item",
    percentPosition: true,
    gutter: 0,
  });
}, [posts]);


// load more button 
const loadMore = async () => {
  setLoading(true);
  await getpost(page + 1, false);
  setPage(page + 1);
  setLoading(false);
};


// dynamic recommendation system 
const categories = [
  "Technology",
  "Health & Wellness",
  "Education & Learning",
  "Lifestyle & Productivity",
  "Entertainment & Pop Culture",
  "General"
];

function storeCategories() {
  const categoryData = categories.reduce((acc, category) => {
    acc[category] = 0; // Initialize each category with count 0
    return acc;
  }, {});

  localStorage.setItem("categoryEngagement", JSON.stringify(categoryData));
}

const resetCategoryEngagement = () => {
  const resetData = categories.reduce((acc, category) => {
      acc[category] = 0;
      return acc;
  }, {});
  localStorage.setItem("categoryEngagement", JSON.stringify(resetData));
  console.log("Category engagement reset to 0");
};

useEffect(() => {
  const storedData = localStorage.getItem("categoryEngagement");
  if (!storedData) {
    console.log("Storing category data for the first time...");
    storeCategories();
  } else {
    console.log("Existing Category Data:", JSON.parse(storedData));
  }
}, []);

  return (

    <div className='row masonry-grid' style={{ marginLeft: "-10px", width: "80vw", }}>
    {posts.map((post) => <Displaypost key={post._id} post={post} />)}

   <button
   onClick={res} 
   className="btn btn-danger btn-sm d-block mx-auto my-3 mb-2"
   >
    the reset button 
   </button>

    {
      <button 
        onClick={loadMore} 
        disabled={loading} 
        className="btn btn-primary btn-sm d-block mx-auto my-3 mb-2"

      >
        {loading ? "Loading..." : "Load More"}
      </button>
    }
  </div>


  )
}
