import React, { useState } from 'react'
import postContext from './postContext';


export default function NoteState(props) {
    const host = "http://localhost:4000"


    const [posts, setPosts] = useState([]);
    const [hasMore, setHasMore] = useState(true);
    const Category = "Technology";


    //get posts 
    const getpost = async (page = 1, category, reset = false) => {
        try {
            const response = await fetch(`${host}/posts?page=${page}&limit=5&category=${encodeURIComponent(category)}`, {
                method: "GET",
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) throw new Error(`HTTP error! Status: ${response.status}`);

            const json = await response.json();

            if (reset) {
                setPosts(json);
            } else {
                setPosts(prevPosts => [...prevPosts, ...json]);
            }

            if (json.length < 5) {
                setHasMore(false);  // No more posts to load
            }
        } catch (error) {
            console.error("Error fetching posts:", error.message);
        }
    };


    // add a post
    const addpost = async (newpost) => {
        const Title = newpost.Title;
        const Subheading = newpost.Subheading;
        const Description = newpost.Description;
        const Category = newpost.Category;


        //API CALL
        const resposne = await fetch(`${host}/posts/newpost`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'auth-token': localStorage.getItem('token')
            },
            body: JSON.stringify({ Title,Subheading, Description,Category })

        });
        const json = resposne.json()
        getpost()
    }





    return (
        <postContext.Provider value={{ posts,addpost,getpost}}>
            {props.children}
        </postContext.Provider>
    )
}
