import React from 'react';
import Notes from './Notes';
import { useContext, useEffect, useState} from 'react'
import noteContext from '../context/noteContext'
export default function Home() {

  const {addnotes} =useContext(noteContext);

  const [state ,setState]=useState({Title:"enter your title here",Description:""});

  onchange=(event)=>{
    setState(event.target.value);
  }
  onclick=()=>{
    addnotes()
  }

  return (
    <div>
      <div>
        <h1 className='container my-3'>Create a new note </h1>
        <div className="mb-3 container">
          <label for="exampleFormControlInput1" className="form-label">Title</label>
          <input type="text" className="form-control" id="exampleFormControlInput1" value={state.Title} onChange={onchange} />
        </div>
        <div className="mb-3 container">
          <label for="exampleFormControlTextarea1" className="form-label">Description</label>
          <textarea className="form-control" id="exampleFormControlTextarea1" rows="3" onchange={onchange} value={state.Description}></textarea>
        </div>
        <div class="col-auto container">
          <button type="submit" class="btn btn-primary mb-3" onClick={onclick}>Add Note</button>
        </div>
      </div>
     <div className='container'>
      <Notes/>
     </div>


    </div>

  )
}
